import React, { useState, useEffect } from "react";
import { Table, Input } from "reactstrap";  // Remover Button pois não é necessário aqui
import { getSortedStudents } from "../services/api"; // Importando a função para buscar os alunos
import StudentDetails from "./StudentDetails"; // Importando o componente StudentDetails

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [strategy, setStrategy] = useState(0); // Armazena a estratégia selecionada
  const [selectedStudent, setSelectedStudent] = useState(null); // Aluno selecionado

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await getSortedStudents(strategy); // Passando a estratégia escolhida
        setStudents(response.data);  // Supondo que a resposta seja um array de alunos
      } catch (error) {
        console.error("Erro ao buscar alunos", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, [strategy]);  // Recarrega quando a estratégia for alterada

  if (loading) {
    return <p>Carregando...</p>;
  }

  const handleStudentClick = (student) => {
    setSelectedStudent(student);  // Atualiza o aluno selecionado
  };

  return (
    <div>
      <h2>Lista de Alunos</h2>

      {/* Dropdown para selecionar a estratégia de ordenação */}
      <Input 
        type="select" 
        value={strategy} 
        onChange={(e) => setStrategy(Number(e.target.value))} 
      >
        <option value={0}>Bubble Sort</option>
        <option value={1}>LINQ Sort</option>
      </Input>

      <Table striped>
        <thead>
          <tr>
            <th>Matrícula</th>
            <th>Nome</th>
            <th>Nota Média</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.registration} onClick={() => handleStudentClick(student)}>
              <td>{student.registration}</td>
              <td>{student.name}</td>
              <td>{student.average}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Exibe os detalhes do aluno selecionado */}
      {selectedStudent && <StudentDetails student={selectedStudent} />}
    </div>
  );
};

export default StudentList;
