import React from 'react';
import { Card, CardBody, CardTitle, CardText } from 'reactstrap';

const StudentDetails = ({ student }) => {
  if (!student) return <div>Selecione um aluno para ver os detalhes.</div>;

  return (
    <Card>
      <CardBody>
        <CardTitle tag="h5">{student.name}</CardTitle>
        <CardText><strong>Matr√≠cula:</strong> {student.registration}</CardText>
        {Object.keys(student.grades).map((subject) => (
          <CardText key={subject}>
            <strong>{subject}:</strong> {student.grades[subject]}
          </CardText>
        ))}
      </CardBody>
    </Card>
  );
};

export default StudentDetails;
