import React, { useState } from 'react';
import { Container, Row, Col } from 'reactstrap';
import StudentList from './components/StudentList';
import StudentDetails from './components/StudentDetails';

const App = () => {
  const [selectedStudent, setSelectedStudent] = useState(null);  // Estado para armazenar o aluno selecionado

  // Função chamada quando um aluno for selecionado na lista
  const handleStudentSelect = (student) => {
    setSelectedStudent(student);
  };

  return (
    <Container>
      <Row className="my-4">
        <Col>
          <h1>Lista de Alunos</h1>
        </Col>
      </Row>
      <Row>
        <Col md="6">
          <StudentList onStudentSelect={handleStudentSelect} />
        </Col>
        <Col md="6">
          <h3>Detalhes do Aluno</h3>
          {selectedStudent ? (
            <StudentDetails student={selectedStudent} />
          ) : (
            <p>Selecione um aluno para ver os detalhes.</p>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default App;
