import React, { useState, useEffect } from "react";
import { Table } from "reactstrap";  // Usando reactstrap para criar a tabela

import { getSortedStudents } from "../services/api"; // Importando a função para buscar os alunos

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        // Supondo que o parâmetro 0 seja a estratégia de Bubble Sort, por exemplo
        const response = await getSortedStudents(0);
        setStudents(response.data);  // Supondo que a resposta seja um array de alunos
      } catch (error) {
        console.error("Erro ao buscar alunos", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <div>
      <h2>Lista de Alunos</h2>
      <Table striped>
        <thead>
          <tr>
            <th>Matrícula</th>
            <th>Nome</th>
            <th>Nota Média</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.registration}>
              <td>{student.registration}</td>
              <td>{student.name}</td>
              <td>{student.averageGrade}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default StudentList;
