import React, { useState, useEffect } from "react";
import { Card, CardBody, CardTitle, CardText, Table, Spinner } from "reactstrap";
import { useParams } from "react-router-dom";  // Para acessar os parâmetros da URL
import { getStudentByRegistration } from "../services/api";  // Função para pegar os detalhes do aluno

const StudentDetails = () => {
  const { registration } = useParams(); // Pegando a matrícula da URL
  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        const response = await getStudentByRegistration(registration); // Passando a matrícula
        setStudent(response.data);  // Atualiza o estado com os detalhes do aluno
      } catch (error) {
        console.error("Erro ao buscar os detalhes do aluno", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudentDetails();
  }, [registration]);  // Recarrega quando a matrícula mudar

  if (loading) {
    return <Spinner color="primary" />;
  }

  // Verificando se o aluno existe e tem as grades
  if (!student || !student.grades) {  // Corrigido para acessar 'grades' com 'g' minúsculo
    return <p>Aluno não encontrado ou sem notas.</p>;
  }

  // Função para verificar se alguma nota é abaixo de 60
  const isGradeBelow60 = (grade) => grade < 60;

  // Verificando se o aluno foi aprovado ou reprovado
  const isApproved = Object.values(student.grades).every(grade => grade >= 60);

  return (
    <div>
      <Card>
        <CardBody>
          <CardTitle tag="h5">{student.name}</CardTitle>  {/* Alterado para 'name' em vez de 'Name' */}
          <CardText><strong>Matrícula:</strong> {student.registration}</CardText>

          {/* Tabela para as notas por matéria */}
          <h5>Notas por Matéria</h5>
          <Table striped>
            <thead>
              <tr>
                <th>Matéria</th>
                <th>Nota</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(student.grades).map(([subject, grade], index) => (
                <tr key={index}>
                  <td>{subject}</td>
                  <td style={{
                    backgroundColor: isGradeBelow60(grade) ? 'red' : 'transparent', // Linha vermelha se a nota for abaixo de 60
                    color: isGradeBelow60(grade) ? 'white' : 'black'  // Texto branco para a linha vermelha
                  }}>
                    {grade}
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>

          {/* Exibindo a nota média */}
          <CardText>
            <strong>Nota Média:</strong> {student.average.toFixed(2)}  {/* Corrigido para 'average' */}
          </CardText>

          {/* Exibindo se o aluno foi aprovado ou não */}
          <CardText style={{ color: isApproved ? 'green' : 'red' }}>
            <strong>Status: </strong> {isApproved ? "Aprovado" : "Reprovado"}  {/* Exibindo em verde ou vermelho */}
          </CardText>
        </CardBody>
      </Card>
    </div>
  );
};

export default StudentDetails;
