import React, { useState, useEffect } from "react";
import { Table, Button } from "reactstrap";
import { Link } from "react-router-dom";
import { getBestStudentsBySubject } from "../services/api";
import { useNavigate } from "react-router-dom";  // Usando para navegação

const BestStudentsList = () => {
  const [bestStudents, setBestStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  const handleStudentClick = (registration) => {
    // Redireciona para o aluno selecionado com base na matrícula
    navigate(`/student/${registration}`);
  };

  useEffect(() => {
    const fetchBestStudents = async () => {
      try {
        const response = await getBestStudentsBySubject();
        setBestStudents(response.data);
      } catch (error) {
        console.error("Erro ao buscar melhores alunos", error);
      } finally {
        setLoading(false);
      }
    };

    fetchBestStudents();
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <div>
      <h3>Melhores Alunos por Matéria</h3>
      <Table striped>
        <thead>
          <tr>
            <th>Matéria</th>
            <th>Nome do Melhor Aluno</th>
            <th>Matricula</th>
            <th>Nota</th>
            <th>Detalhes</th>
          </tr>
        </thead>
        <tbody>
          {bestStudents.map((entry, index) => (
            <tr key={index}>
              <td>{entry.subject}</td>
              <td>{entry.bestStudent.name}</td>
              <td>{entry.bestStudent.registration}</td>
              <td>{entry.grade}</td>
