import React, { useEffect, useState } from 'react';
import { ListGroup, ListGroupItem,  Spinner } from 'reactstrap';
import { getSortedStudents } from '../services/api'; // Função para obter os alunos

const StudentList = ({ onStudentSelect }) => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  // Função para buscar alunos ordenados
  const fetchStudents = async () => {
    setLoading(true);
    try {
      const response = await getSortedStudents('bubble'); // Aqui estamos usando o "bubble" como exemplo
      setStudents(response.data);
    } catch (error) {
      console.error('Erro ao buscar alunos:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  if (loading) {
    return <Spinner color="primary" />;
  }

  return (
    <ListGroup>
      {students.map((student) => (
        <ListGroupItem key={student.registration} tag="button" action onClick={() => onStudentSelect(student)}>
          {student.name} - {student.registration}
        </ListGroupItem>
      ))}
    </ListGroup>
  );
};

export default StudentList;
