import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Spinner } from 'reactstrap';
import axios from 'axios';
import StudentList from './components/StudentList';
import StudentDetails from './components/StudentDetails';
import config from './config.js';

const App = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [sortStrategy, setSortStrategy] = useState(0); // Estratégia de ordenação: 0 - BubbleSort, 1 - LinqSort
  const [selectedStudent, setSelectedStudent] = useState(null); // Aluno selecionado para mostrar detalhes

  useEffect(() => {
    const fetchStudents = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`${config.apiUrl}/Student/sorted?strategy=${sortStrategy}`);
        setStudents(response.data);
      } catch (error) {
        console.error('Erro ao buscar alunos:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, [sortStrategy]);

  const handleSelectStudent = (student) => {
    setSelectedStudent(student); // Definindo o aluno selecionado
  };

  return (
    <Container>
      <Row className="my-4">
        <Col>
          <h1>Lista de Alunos</h1>
        </Col>
      </Row>
      <Row>
        <Col md="6">
          {loading ? (
            <Spinner color="primary" />
          ) : (
            <StudentList students={students} onSelectStudent={handleSelectStudent} />
          )}
        </Col>
        <Col md="6">
          <h3>Detalhes do Aluno</h3>
          <StudentDetails student={selectedStudent} />
        </Col>
      </Row>
    </Container>
  );
};

export default App;
